import {
  StyleSheet,
  Text,
  View,
  Pressable,
  ImageBackground,
  Image,
  Alert,
  TouchableOpacity,
  ScrollView,
} from "react-native";
import React, { useEffect, useState } from "react";
import Icon from "react-native-elements/dist/icons/Icon";
import { LineChart } from "react-native-chart-kit";
import Footer from "../footer";
import { Dimensions } from "react-native";
import { getByNftId } from "../../services/nfts/index";
import { RFValue } from "react-native-responsive-fontsize";
import LittleNfts from "../nfts/littleNfts";
import { deleteRun } from "../../services/runs/index";
export default function RunScreen({ navigation, route }) {
  const [run, setRun] = useState(JSON.parse(route.params).run);
  const [nft, setNft] = useState(0);
  useEffect(() => {
    myFunction();
  }, []);

  const mbsImage = [
    require("../../assets/mb/lvl1.png"),
    require("../../assets/mb/lvl2.png"),
    require("../../assets/mb/lvl3.png"),
    require("../../assets/mb/lvl4.png"),
    require("../../assets/mb/lvl5.png"),
    require("../../assets/mb/lvl6.png"),
    require("../../assets/mb/lvl7.png"),
    require("../../assets/mb/lvl8.png"),
    require("../../assets/mb/lvl9.png"),
    require("../../assets/mb/lvl10.png"),
  ];

  async function myFunction() {
    try {
      var tempNft = await getByNftId(run.nftId);
      if (tempNft) {
        setNft(tempNft);
      }
    } catch (error) {
      Alert.alert(error);
      setNft(0);
    }
  }

  const data = {
    labels: ["January", "February", "March", "April", "May", "June"],
    datasets: [
      {
        data: [20, 45, 28, 80, 99, 43],
      },
    ],
  };

  function minuteConverter(time) {
    const [h, m, s] = time.split(":");
    const value = +h + +m / 60 + s / 3600;
    return value.toFixed(6);
  }

  function dateFormat(date) {
    let year = date.getFullYear();
    let month = date.getMonth();
    let day = date.getDate();
    let hour = date.getHours();
    let minute = date.getMinutes();
    if (minute < 10) {
      minute = "0" + minute;
    }
    if (hour < 10) {
      hour = "0" + hour;
    }
    if (month < 10) {
      month = "0" + month;
    }
    if (day < 10) {
      day = "0" + day;
    }
    var date = `${day}/${month}/${year} ${hour}:${minute}`;
    return date;
  }

  return (
    <View style={{ width: "100%", height: "100%", alignItems: "center" }}>
      {/* Header */}
      <View
        style={{
          top: 0,
          backgroundColor: "#E0FEF3",
          width: "100%",
          height: "42%",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <TouchableOpacity
          style={{ ...styles.return, top: "18%" }}
          onPressIn={() => navigation.navigate("AllRunsScreen")}
        >
          <Icon type="antdesign" name="left" size={RFValue(20, 800)} color="black"></Icon>
        </TouchableOpacity>
        {/* Top part */}
        <View
          style={{
            top: "18%",
            width: "60%",
            height: "15%",
            flexDirection: "row",
            position: "absolute",
            justifyContent: "space-between",
          }}
        >
          <Text style={{ fontSize: RFValue(16, 800), fontWeight: "600" }}>
            {dateFormat(new Date(run.date))}
          </Text>
          <View
            style={{
              flexDirection: "row",
              width: "30%",
              justifyContent: "space-between",
              alignItems: "flex-end",
            }}
          >
            <Text style={{ fontSize: RFValue(48, 800), fontWeight: "700" }}>
              {(run.gst / minuteConverter(run.duration) / 60).toFixed(1)}
            </Text>
            <Text style={{}}>GST/Min</Text>
          </View>
        </View>
        <View
          style={{
            top: "30%",
            width: "80%",
            height: "60%",
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          {/* Nfts */}
          <Pressable
            style={{
              width: "53%",
              height: "100%",
              backgroundColor: "white",
              borderRadius: 20,
              alignItems: "center",
            }}
            onPress={() => {
              nft === 0
                ? Alert.alert("Please add your nft on the nft screen!")
                : "";
            }}
          >
            <LittleNfts data={nft}></LittleNfts>
            {nft.fileNameIncreased === null ? (
              <Text style={{ fontSize: RFValue(10, 800), marginTop: "2%", textAlign: "center", color: "red" }}>
                Please add your sneaker with increased stats!
              </Text>
            ) : (
              <View></View>
            )}
          </Pressable>

          <View
            style={{
              flexDirection: "column",
              width: "38%",
              justifyContent: "space-evenly",
              height: "70%",
            }}
          >
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <Image
                source={require("../../assets/gst.png")}
                style={{ width: 30, height: 30, resizeMode: "contain" }}
              ></Image>
              <Text style={{ fontSize: RFValue(24, 800), fontWeight: "700" }}>
                + {run.gst}
              </Text>
            </View>
            {run.mbLvl ? (
              <View
                style={{
                  flexDirection: "row",
                  alignItems: "center",
                  justifyContent: "space-between",
                }}
              >
                <Image
                  source={mbsImage[run.mbLvl - 1]}
                  style={{ width: 30, height: 30, resizeMode: "contain" }}
                ></Image>
                <Text style={{ fontSize: RFValue(24, 800), fontWeight: "700" }}>
                  + 1
                </Text>
              </View>
            ) : (
              <View></View>
            )}

            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <Image
                source={require("../../assets/energy.png")}
                style={{ width: 30, height: 30, resizeMode: "contain" }}
              ></Image>
              <Text style={{ fontSize: RFValue(24, 800), fontWeight: "700" }}>
                - {run.energy}
              </Text>
            </View>
          </View>
        </View>
      </View>
      <View
        style={{
          justifyContent: "space-between",
          flexDirection: "row",
          width: "100%",
        }}
      >
        <View
          style={{
            width: "75%",
          }}
        ></View>
        <View
          style={{
            justifyContent: "space-between",
            flexDirection: "row",
            width: "20%",
            marginRight: "5%",
          }}
        >
          <Pressable
            style={{
              backgroundColor: "#9DF8B6",
              justifyContent: "center",
              alignContent: "center",
              width: 32,
              height: 32,
              borderRadius: 20,
              borderWidth: 1,
              borderColor: "black",
              shadowOpacity: 1,
              shadowRadius: 1,
              shadowOffset: {
                width: 1,
                height: 1,
              },
            }}
            onPress={() => Alert.alert("Soon available")}
          >
            <Icon
              style={{ width: "100%" }}
              size={RFValue(20, 800)}
              type="material-community"
              name="pencil-outline"
              color="black"
            ></Icon>
          </Pressable>
          <Pressable
            style={{
              backgroundColor: "#9DF8B6",
              justifyContent: "center",
              alignContent: "center",
              width: 32,
              height: 32,
              borderRadius: 20,
              borderWidth: 1,
              borderColor: "black",
              shadowOpacity: 1,
              shadowRadius: 1,
              shadowOffset: {
                width: 1,
                height: 1,
              },
            }}
            onPress={async () => {
              await deleteRun(run.id);
              JSON.parse(route.params).myFunction();
              navigation.navigate("AllRunsScreen");
            }}
          >
            <Icon
              style={{ width: "100%" }}
              size={RFValue(20, 800)}
              type="material-community"
              name="trash-can-outline"
              color="black"
            ></Icon>
          </Pressable>
        </View>
      </View>

      {/* <Text>test</Text> */}
      <View
        style={{
          justifyContent: "space-between",
          flexDirection: "row",
          alignItems: "center",
          width: "80%",
          height: "10%",
          paddingBottom: "5%",
        }}
      >
        <View
          style={{
            justifyContent: "space-between",
            flexDirection: "column",
            alignItems: "center",
            height: "50%",
          }}
        >
          <Text style={{ fontSize: RFValue(20, 800), fontWeight: "700" }}>
            {run.km}
          </Text>
          <Text style={{ fontSize: RFValue(12, 800), fontWeight: "500" }}>
            Km
          </Text>
        </View>
        <View
          style={{
            justifyContent: "space-between",
            flexDirection: "column",
            alignItems: "center",
            height: "50%",
          }}
        >
          <Text style={{ fontSize: RFValue(20, 800), fontWeight: "700" }}>
            {run.duration}
          </Text>
          <Text style={{ fontSize: RFValue(12, 800), fontWeight: "500" }}>
            Time
          </Text>
        </View>
        <View
          style={{
            justifyContent: "space-between",
            flexDirection: "column",
            alignItems: "center",
            height: "50%",
          }}
        >
          <Text style={{ fontSize: RFValue(20, 800), fontWeight: "700" }}>
            {run.steps}
          </Text>
          <Text style={{ fontSize: RFValue(12, 800), fontWeight: "500" }}>
            Steps
          </Text>
        </View>
      </View>
      <LineChart
        data={{
          labels: ["", "Soon here a chart to check your perfomances!"],
          datasets: [
            {
              data: [
                0,
              ],
            },
          ],
        }}
        width={Dimensions.get("window").width - 50}
        height={Dimensions.get("window").height * 0.25}
        yAxisInterval={1}
        chartConfig={{
          backgroundGradientFrom: "white",
          backgroundGradientTo: "white",
          decimalPlaces: 0,
          color: (opacity = 1) => "#4DC6F4",
          labelColor: (opacity = 1) => `#4DC6F4`,
          propsForDots: {
            r: "3",
            strokeWidth: "1",
            stroke: "white",
          },
        }}
        getDotColor={(dataPoint, dataPointIndex) => {
          //based on condition we return the color as string
          if (dataPointIndex === 2) return "red";
        }}
        getDotProps={(dataPoint, dataPointIndex) => {
          //based on condition we return the color as string
          if (dataPointIndex === 2)
            return {
              r: "10",
              strokeWidth: "1",
              stroke: "white",
            };
        }}
        style={{
          borderRadius: 12,
          borderWidth: 1,
          borderColor: "black",
          shadowColor: "black",
          shadowOpacity: 1,
          shadowRadius: 1,
          shadowOffset: {
            width: 4,
            height: 4,
          },
        }}
        bezier
      />
      {/* <Footer styles={styles}></Footer> */}
    </View>
  );
}

